    # Dynamic        # PPPoE    class LAN(object):    ## level1    LAN_SETTING = Widget(name='LAN_SETTING',                        xpath="//a[@href='/userRpm/NetworkCfgRpm.htm' and @class='L1']",                        type=ConfigConstant.WIDGET_TYPE_LINK,                        belongs=('NETWORK_SETTING', ),                        level=1)                            ## level2    LAN_SETTING_IP = Widget(name='LAN_SETTING_IP',                        xpath="//input[@id='lanip']",                        type=ConfigConstant.WIDGET_TYPE_TEXT_BOX,                        belongs=('LAN_SETTING', ),                        level=2)        LAN_SETTING_MASK = Widget(name='LAN_SETTING_MASK',                        xpath="//select[@name='lanmask']",                        type=ConfigConstant.WIDGET_TYPE_LIST,                        belongs=('LAN_SETTING', ),                        level=2)                            LAN_SETTING_SAVE = Widget(name='LAN_SETTING_SAVE',                        xpath="//input[@name='Save']",                        type=ConfigConstant.WIDGET_TYPE_BUTTON,                        belongs=('LAN_SETTING', ),                        level=2)        LAN_SETTING_HELP = Widget(name='LAN_SETTING_HELP',                        xpath="//input[@id='help']",                        type=ConfigConstant.WIDGET_TYPE_BUTTON,                        belongs=('LAN_SETTING', ),                        level=2)        ## level3    LAN_SETTING_HELP_BACK = Widget(name='LAN_SETTING_HELP_BACK',                        xpath="//input[@name='back']",                        type=ConfigConstant.WIDGET_TYPE_BUTTON,                        belongs=('LAN_SETTING_HELP', ),                        level=3)                            LAN_SETTING_IP_ERROR_BACK = Widget(name='LAN_SETTING_IP_ERROR_BACK',                        xpath="//input[@name='Back']",                        type=ConfigConstant.WIDGET_TYPE_BUTTON,                        belongs=('LAN_SETTING_SAVE', ),                        level=3)                            LAN_SETTING_MASK_INPUT = Widget(name='LAN_SETTING_MASK_INPUT',                        xpath="//input[@name='inputMask']",                        type=ConfigConstant.WIDGET_TYPE_TEXT_BOX,                        belongs=('LAN_SETTING_MASK', 'index=3'),                        level=3)                            LAN_SETTING_MASK_INPUT_ERROR_BACK = Widget(name='LAN_SETTING_MASK_INPUT_ERROR_BACK',                        xpath="//input[@name='Back']",                        type=ConfigConstant.WIDGET_TYPE_BUTTON,                        belongs=('LAN_SETTING_SAVE', ),                        level=3)class MacClone(object):    ## level1    MACCLONE_SETTING = Widget(name='MACCLONE_SETTING',                        xpath="//a[@href='/userRpm/MacCloneCfgRpm.htm' and @class='L1']",                        type=ConfigConstant.WIDGET_TYPE_LINK,                        belongs=('NETWORK_SETTING', ),                        level=1)          ## level2    MACCLONE_SETTING_CLONE = Widget(name='MACCLONE_SETTING_CLONE',                        xpath="//input[@name='clone']",                        type=ConfigConstant.WIDGET_TYPE_BUTTON,                        belongs=('MACCLONE_SETTING', ),                        level=2)            MACCLONE_SETTING_RESTORE = Widget(name='MACCLONE_SETTING_RESTORE',                        xpath="//input[@name='restore1']",                        type=ConfigConstant.WIDGET_TYPE_BUTTON,                        belongs=('MACCLONE_SETTING', ),                        level=2)                              MACCLONE_SETTING_WAN_MAC = Widget(name='MACCLONE_SETTING_WAN_MAC',                        xpath="//input[@name='mac1']",                        type=ConfigConstant.WIDGET_TYPE_TEXT_BOX,                        belongs=('MACCLONE_SETTING', ),                        level=2)                             MACCLONE_SETTING_PC_MAC = Widget(name='MACCLONE_SETTING_PC_MAC',                        xpath="//input[@name='pcmac']",                        type=ConfigConstant.WIDGET_TYPE_TEXT_BOX,                        belongs=('MACCLONE_SETTING', ),                        is_enabled=False,                        level=2)                             MACCLONE_SETTING_SAVE = Widget(name='MACCLONE_SETTING_SAVE',                        xpath="//input[@name='Save']",                        type=ConfigConstant.WIDGET_TYPE_BUTTON,                        belongs=('MACCLONE_SETTING', ),                        level=2)         MACCLONE_SETTING_HELP = Widget(name='MACCLONE_SETTING_HELP',                        xpath="//input[@name='help']",                        type=ConfigConstant.WIDGET_TYPE_BUTTON,                        belongs=('MACCLONE_SETTING', ),                        level=2)                             ## level3    MACCLONE_SETTING_HELP_BACK = Widget(name='MACCLONE_SETTING_HELP_BACK',                        xpath="//input[@name='back']",                        type=ConfigConstant.WIDGET_TYPE_BUTTON,                        belongs=('MACCLONE_SETTING_HELP', ),                        level=3) class NetWork(WAN, LAN, MacClone):    ## level0    NETWORK_SETTING = Widget(name='NETWORK_SETTING',                            xpath="//a[@href='/userRpm/WanCfgRpm.htm' and @class='L1']",                            type=ConfigConstant.WIDGET_TYPE_LINK,                            level=0)    